import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class LoanService {
  private apiUrl = 'http://localhost:8080/api/loans';

  constructor(private http: HttpClient) {}

  applyForLoan(accountId: number, amount: number, interestRate: number, tenure: number): Observable<any> {
    return this.http.post(`${this.apiUrl}/apply`, { accountId, amount, interestRate, tenure });
  }

  approveLoan(loanId: number): Observable<any> {
    return this.http.post(`${this.apiUrl}/${loanId}/approve`, {});
  }

  getLoansForAccount(accountId: number): Observable<any> {
    return this.http.get(`${this.apiUrl}/${accountId}/details`);
  }
}
