import { Component, OnInit } from '@angular/core';
import { RouterModule } from '@angular/router';
import { AccountService } from '../account.service';  // Import the AccountService

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css']
})
export class AccountComponent implements OnInit {
acc:any={
  accountNumber:'',
  amount:'',
  accountHolderName:''
};

  constructor(private accountService: AccountService) {}

  ngOnInit(): void {
    this.loadAccountDetails();
  }

  loadAccountDetails() {
    // Call a service to load the account details (you can implement this based on your backend)
    this.accountService.getAccountDetails().subscribe((data:any) => {
      this.acc = Object.values(data);

      });
  }

  deposit() {
    this.accountService.deposit(this.acc.accountNumber,this.acc.amount).subscribe(
      (data:any) => {
        this.acc = Object.values(data);  // Convert object to array

        console.log('Deposit successful', data);
      },
      (error) => {
        console.error('Error depositing', error);
      }
    );
  }

  withdraw() {
    this.accountService.withdraw(this.acc.accountNumber, this.acc.amount).subscribe(
      (data) => {
        this.acc = data;  // Update account details after withdrawal
        console.log('Withdrawal successful', data);
      },
      (error) => {
        console.error('Error withdrawing', error);
      }
    );
  }
}
<div>
  <table style="width: 100%; border-collapse: collapse; margin-bottom: 20px;">
    <thead>
      <tr>
        <th style="padding: 8px; border: 1px solid #ddd; text-align: left;">Account Number</th>
        <th style="padding: 8px; border: 1px solid #ddd; text-align: left;">Account Holder Name</th>
        <th style="padding: 8px; border: 1px solid #ddd; text-align: left;">Balance</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let dat of acc" style="border-bottom: 1px solid #ddd;">
        <td style="padding: 8px; text-align: left;">{{ dat.accountNumber }}</td>
        <td style="padding: 8px; text-align: left;">{{ dat.accountHolderName }}</td>
        <td style="padding: 8px; text-align: left;">{{ dat.amount | currency }}</td>
      </tr>
    </tbody>
  </table>

  <div>
    <h2>Deposit</h2>
    <mat-form-field style="width: 100%; margin-bottom: 10px;">
      <input matInput [(ngModel)]="acc.accountNumber" placeholder="Account Number" />
    </mat-form-field>
    <mat-form-field style="width: 100%; margin-bottom: 10px;">
      <input matInput [(ngModel)]="acc.amount" placeholder="Amount to deposit" />
    </mat-form-field>
    <button mat-raised-button (click)="deposit()" style="width: 100%;">Deposit</button>
  </div>

  <div>
    <h2>Withdraw</h2>
    <mat-form-field style="width: 100%; margin-bottom: 10px;">
      <input matInput [(ngModel)]="acc.accountNumber" placeholder="Account Number" />
    </mat-form-field>
    <mat-form-field style="width: 100%; margin-bottom: 10px;">
      <input matInput [(ngModel)]="acc.amount" placeholder="Amount to withdraw" />
    </mat-form-field>
    <button mat-raised-button (click)="withdraw()" style="width: 100%;">Withdraw</button>
  </div>
</div>
