import { Component, OnInit } from '@angular/core';
import { LoanService } from '../loan.service';

@Component({
  selector: 'app-loan',
  templateUrl: './loan.component.html',
  styleUrls: ['./loan.component.css']
})
export class LoanComponent implements OnInit {
  loans: any=[];
  displayedColumns: string[] = ['id', 'loanAmount', 'status'];

  accountId: number = 1; // Change to the actual account ID

  constructor(private loanService: LoanService) {}

  ngOnInit(): void {
    this.loadLoans();
  }

  loadLoans() {
    this.loanService.getLoansForAccount(this.accountId).subscribe((data) => {
      this.loans = data;
    });
  }

  applyForLoan(amount: number, interestRate: number, tenure: number) {
    this.loanService.applyForLoan(this.accountId, amount, interestRate, tenure).subscribe(() => {
      this.loadLoans();
    });
  }

  approveLoan(loanId: number) {
    this.loanService.approveLoan(loanId).subscribe(() => {
      this.loadLoans();
    });
  }
}
