import { Component, OnInit } from '@angular/core';
import { AccountService } from 'src/app/service/account.service';

@Component({
  selector: 'app-userhome',
  templateUrl: './userhome.component.html',
  styleUrls: ['./userhome.component.css']
})
export class UserhomeComponent implements OnInit {
  accountHolderName: string = '';
  balance: number | null = null;
  errorMessage: string = '';

  constructor(private accountService: AccountService) {}

  ngOnInit(): void {
    // Retrieve accountHolderName from localStorage (set after login)
    this.accountHolderName = localStorage.getItem('accountHolderName') || '';

    // If logged in, check balance
    if (this.accountHolderName) {
      this.checkBalance();
    }
  }

  checkBalance(): void {
    // Call the service to get the balance for the logged-in user
    this.accountService.getBalance(this.accountHolderName).subscribe(
      (response) => {
        this.balance = response;
        this.errorMessage = '';
      },
      (error) => {
        this.balance = null;
        this.errorMessage = 'Account not found or error retrieving balance';
      }
    );
  }
}
<div class="home-container">
  <div class="hero-section">
    <h1>Welcome to Your Banking Portal</h1>
    <p>Manage your accounts, apply for loans, deposit and withdraw easily.</p>
    <button mat-raised-button color="primary" routerLink="/deposit">Deposit Funds</button>
    <button mat-raised-button color="accent" routerLink="/withdraw">Withdraw Funds</button>
    <button mat-raised-button color="warn" routerLink="/loan-application">Apply for Loan</button>
  </div>

  <div class="balance-section">
    <h2>Account Balance</h2>
    <div *ngIf="balance !== null; else loading">
      <p>Your balance is: {{ balance | currency }}</p>
    </div>
    <ng-template #loading>
      <p *ngIf="errorMessage">{{ errorMessage }}</p>
      <p *ngIf="!errorMessage">Loading balance...</p>
    </ng-template>
  </div>

  <div class="feature-section">
    <div class="feature-card">
      <mat-card>
        <mat-card-header>
          <mat-card-title>Deposit Funds</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <p>Deposit money into your account with ease.</p>
          <button mat-raised-button color="accent" routerLink="/deposit">Deposit Now</button>
        </mat-card-content>
      </mat-card>
    </div>

    <div class="feature-card">
      <mat-card>
        <mat-card-header>
          <mat-card-title>Withdraw Funds</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <p>Withdraw money from your account whenever needed.</p>
          <button mat-raised-button color="warn" routerLink="/withdraw">Withdraw Now</button>
        </mat-card-content>
      </mat-card>
    </div>

    <div class="feature-card">
      <mat-card>
        <mat-card-header>
          <mat-card-title>Apply for Loan</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          <p>Apply for a loan easily with competitive interest rates.</p>
          <button mat-raised-button color="accent" routerLink="/loan-application">Apply Now</button>
        </mat-card-content>
      </mat-card>
    </div>
  </div>
</div>
